const INITIAL_STATE = {
	articles: [],
};

function articleReducer(state = INITIAL_STATE, action) {
	switch (action.type) {
		case "LOADARTICLES": {
			return {
				...state,
				articles: action.payload,
			};
		}
	}
}

export default articleReducer;
