import React, { useState } from "react";
import { useDispatch } from "react-redux";
import "./Form.css";

export default function AddArticle() {
	// declaration state
	const [article, setArticle] = useState({
		title: "",
		body: "",
	});

	// envoie des données vers le store
	const dispatch = useDispatch();

	// gestion du form par fn
	const handleForm = (e) => {
		// bloque le reload
		e.preventDefault();
		dispatch({
			type: "ADDARTICLE",
			payload: article,
		});
		setArticle({
			title: "",
			body: "",
		});
	};
	// gestion des inputs par fn
	const handleInputs = (e) => {
		if (e.target.classList.contains("inp-title")) {
			// on remplace la prop  title de l'object article
			const newObjState = { ...article, title: e.target.value };
			setArticle(newObjState);
		} else if (e.target.classList.contains("inp-body")) {
			// on remplace la prop  body de l'object article
			const newObjState = { ...article, body: e.target.value };
			setArticle(newObjState);
		}
	};

	const showState = () => {
		console.log(article);
	};
	return (
		<>
			<h1 className="title-form">Ecrire un Article</h1>
			<form onSubmit={handleForm} className="container-form">
				<label htmlFor="title">Titre</label>
				<input
					className="inp-title"
					onChange={handleInputs}
					value={article.title}
					type="text"
					id="text"
					placeholder="Entrez un Titre"
					required
				/>
				<label htmlFor="article">Votre article</label>
				<textarea
					className="inp-body"
					onChange={handleInputs}
					value={article.body}
					type="text"
					id="text"
					placeholder="Votre Article"
					required
				/>

				<button onClick={showState} className="btn-form">
					Poster l'article
				</button>
			</form>
		</>
	);
}
