import React from "react";
import "./Form.css";

export default function AddArticle() {
	return (
		<>
			<div lang="fr">
				<h1 className="title-form">Ecrire un Article</h1>
				<form className="container-form">
					<label htmlFor="title">Titre</label>
					<input type="text" id="text" placeholder="Entrez un Titre" required />
					<label htmlFor="article">Votre article</label>
					<textarea type="text" id="text" placeholder="Votre Article" required />

					<button className="btn-form">Poster l'article</button>
				</form>
			</div>
		</>
	);
}
